mapper.select:
SELECT yps.code as permission FROM yy_permissions yps LEFT JOIN yy_permissions_role_ref yprr ON yps.id = yprr.permissions_id WHERE 1=1 AND yps.is_valid='Y' AND yprr.role_id
AND yps.is_button=#{isButton}
IN
#{item}

SELECT yp.id, yp.name as title, yp.url as href, yp.parent_id as parentId, yp.is_button as isButton, yp.code as permission, yp.target, yp.image as icon FROM yy_permissions yp INNER JOIN (SELECT DISTINCT yprr.permissions_id FROM yy_user_role_ref yurr INNER JOIN yy_permissions_role_ref yprr ON yurr.role_id=yprr.role_id WHERE yurr.is_valid='Y' AND yprr.is_valid='Y'
AND yurr.user_id=#{userId}
) a ON yp.id= a.permissions_id
WHERE yp.is_valid='Y'
AND yp.is_button=#{isButton}
